import React, { useState } from 'react';
import './App.css';
import Person from './Person/Person';

const App = props => {

  const [personsState, setPersonsState] = useState({
    persons : [
      {name: 'David', age: 44},
      {name: 'Cris', age: 41},
      {name: 'Alonso', age: 7},
      {name: 'Helena', age: 4}
    ],
    otherState : "Some ohter value"
  });

  console.log(personsState);

  const switchNameHandler = () => {
    let newPersons = [...personsState.persons]
    newPersons[0].name= "David G."
    newPersons[1].name="Cristina"
    newPersons[1].age=40 
    setPersonsState({persons: newPersons});
  }

  return (
    <div className="App">
    <h1>Hi, I'm a React App</h1>
    <p>This is really working</p>
    <button onClick={switchNameHandler}>Switch name</button>

    <Person name={personsState.persons[0].name} age={personsState.persons[0].age}/>
    <Person name={personsState.persons[1].name} age={personsState.persons[1].age}/>
    <Person name={personsState.persons[2].name} age={personsState.persons[2].age}/>
    <Person name={personsState.persons[3].name} age={personsState.persons[3].age}/>
  </div>    
  )
}

export default App;


