import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons : [
      {name: 'David', age: 44},
      {name: 'Cris', age: 41},
      {name: 'Alonso', age: 7},
      {name: 'Helena', age: 4}
    ]
  }

  switchNameHandler = (newName, newAge) => {
    //DON'T DO THIS: personsState.persons[0].name = "David G."
    let newPersons = [...this.state.persons]
    newPersons[1].name=newName
    newPersons[1].age=newAge 
    this.setState({persons: newPersons});
  }

  nameChangeHandler = (event) => {
    let newPersons = [...this.state.persons]
    newPersons[1].name=newName
    newPersons[1].age=newAge 
    this.setState({persons: newPersons});
  }

  render() {
    return (
      <div className="App">
        <h1>Hi, I'm a React App</h1>
        <p>This is really working</p>
        <button onClick={() => this.switchNameHandler('Cristina!', 40)}>Switch name</button>

        <Person 
          name={this.state.persons[0].name} 
          age={this.state.persons[0].age}/>
        <Person 
          name={this.state.persons[1].name} 
          age={this.state.persons[1].age}/>
        <Person click={this.switchNameHandler.bind(this, 'Cris!', 39)}
          name={this.state.persons[2].name} 
          age={this.state.persons[2].age}>
            MyHobbies: bikes, football and playing drums.
        </Person>
        <Person 
          name={this.state.persons[3].name} 
          age={this.state.persons[3].age}/>
      </div>
    );  
  }
}
export default App;
